pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "./NodeManager.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

error NOT_SUPPORTED_TOKEN();
error TOKEN_EXIST();
error TOKEN_DOESNT_EXIST();

// NODE MAGAER ERRORS
error OWNER_ADDRESS_ZERO();
error WRONGE_SIG();
error WRONG_CHAIN();

// transfer eth error
error TRANSFER_FAILED();

contract FileBridge is
    Initializable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    NodeManagement,
    EIP712
{
    using SafeERC20 for IERC20;
    using SafeERC20 for IERC20Mintable;
    using Counters for Counters.Counter;

    mapping(address => uint256) private fees;
    mapping(address => address) public tokenToWTokenMap;
    mapping(address => Counters.Counter) private _nonces;

    bytes32 private constant _REDEEM_TOKEN_TYPEHASH =
        keccak256(
            "redeemToken(address to,uint256 chainId,address token,uint256 amount,uint256 nonce)"
        );
    bytes32 private constant _REDEEM_F_TOKEN_TYPEHASH =
        keccak256(
            "redeemFToken(address to,uint256 chainId,address token,uint256 amount,uint256 nonce)"
        );
    bytes32 private constant _REDEEM_NATIVE_TOKEN_TYPEHASH =
        keccak256(
            "redeemNativeToken(address to,uint256 chainId,uint256 amount,uint256 nonce)"
        );

    uint256 public constant bridgeVersion = 1;
    // address CHAINLINK_PRICE_FEED;
    uint256 public startBlockNumber;
    address payable public WFIL_ADDRESS;

    // Flags execution of contract initialization.
    bool internal isInitialized;

    receive() external payable {}

    constructor() EIP712("FileBridge", "1") {}

    function initialize(
        address _owner,
        address[] memory _members,
        uint256 _honestThreshold
    ) external initializer {
        startBlockNumber = block.number;
        _setupRole(DEFAULT_ADMIN_ROLE, _owner);
        _setupRole(GOVERNANCE_ROLE, _owner);
        __AccessControl_init();
        // NODE MANAGER
        if (_owner == address(0)) revert OWNER_ADDRESS_ZERO();
        for (uint8 i = 0; i < _members.length; i++) {
            _setupRole(GUARDIAN_ROLE, _members[i]);
        }
        acceptebleThreshold = _honestThreshold;
        __AccessControl_init();

        status = Status.Active;
        isInitialized = true;
    }

    function setWethAddress(
        address payable _wethAddress
    ) external onlyRole(GOVERNANCE_ROLE) {
        WFIL_ADDRESS = _wethAddress;
    }

    event TokenAddedToList(address _token, address _WToken);

    event TokenRemovedFromList(address _token);

    event TokenChangedInList(
        address _token,
        address _WTokenOld,
        address _WTokenNew
    );

    event TokenDeposited(
        address indexed to,
        uint256 chainId,
        IERC20 token,
        uint256 amount
    );

    event TokenRedeemed(
        address indexed to,
        uint256 chainId,
        IERC20 token,
        uint256 amount
    );

    event FTokenDeposited(
        address indexed to,
        uint256 chainId,
        IFToken token,
        uint256 amount
    );

    event FTokenRedeemed(
        address indexed to,
        uint256 chainId,
        IFToken token,
        uint256 amount
    );

    event NativeTokenDeposited(
        address indexed to,
        uint256 chainId,
        uint256 amount
    );

    event NativeTokenRedeemed(
        address indexed to,
        uint256 chainId,
        uint256 amount
    );

    // VIEW FUNCTIONS ***/
    function getFeeBalance(
        address tokenAddress
    ) external view returns (uint256) {
        return fees[tokenAddress];
    }

    function redeemTokenHashGenerator(
        address to,
        uint256 chainId,
        IERC20 token,
        uint256 amount,
        uint256 nonce
    ) external view returns (bytes32 hash) {
        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_TOKEN_TYPEHASH,
                to,
                chainId,
                address(token),
                amount,
                nonce
            )
        );

        hash = _hashTypedDataV4(structHash);
    }

    function redeemFTokenHashGenerator(
        address to,
        uint256 chainId,
        IERC20 fToken,
        uint256 amount,
        uint256 nonce
    ) external view returns (bytes32 hash) {
        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_F_TOKEN_TYPEHASH,
                to,
                chainId,
                address(fToken),
                amount,
                nonce
            )
        );

        hash = _hashTypedDataV4(structHash);
    }

    function redeemNativeTokenHashGenerator(
        address to,
        uint256 chainId,
        uint256 amount,
        uint256 nonce
    ) external view returns (bytes32 hash) {
        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_NATIVE_TOKEN_TYPEHASH,
                to,
                chainId,
                amount,
                nonce
            )
        );

        hash = _hashTypedDataV4(structHash);
    }

    /**
     * @dev See {file bridge nonces}.
     */
    function nonces(address owner) public view returns (uint256) {
        return _nonces[owner].current();
    }

    // ADD WRAPPED TOKEN FUNCTION ***/
    /**
     * * @notice add the token to list of acceptable token for brdige
     * * @param _token ERC20 token
     * * @param _fToken Wrapped ERC20 token
     */

    function addWToken(
        address _token,
        address _fToken
    ) external onlyRole(GOVERNANCE_ROLE) {
        if (tokenToWTokenMap[_token] != address(0)) revert TOKEN_EXIST();
        tokenToWTokenMap[_token] = _fToken;
        emit TokenAddedToList(_token, _fToken);
    }

    // ADD WRAPPED TOKEN FUNCTION ***/
    /**
     * * @notice remove the token from list of acceptable token for brdige
     * * @param _token ERC20 token
     */

    function removeWToken(address _token) external onlyRole(GOVERNANCE_ROLE) {
        if (tokenToWTokenMap[_token] == address(0)) revert TOKEN_DOESNT_EXIST();
        tokenToWTokenMap[_token] = address(0);
        emit TokenRemovedFromList(_token);
    }

    // ADD WRAPPED TOKEN FUNCTION ***/
    /**
     * * @notice change the token in list of acceptable token for brdige
     * * @param _token ERC20 token
     * * @param _fToken Wrapped ERC20 token
     */

    function changeWToken(
        address _token,
        address _fToken
    ) external onlyRole(GOVERNANCE_ROLE) {
        if (tokenToWTokenMap[_token] == address(0)) revert TOKEN_DOESNT_EXIST();
        emit TokenChangedInList(_token, tokenToWTokenMap[_token], _fToken);
        tokenToWTokenMap[_token] = _fToken;
    }

    // FEE FUNCTIONS ***/
    /**
     * * @notice withdraw specified ERC20 token fees to a given address
     * * @param token ERC20 token in which fees acccumulated to transfer
     * * @param to Address to send the fees to
     */
    function withdrawFees(
        IERC20 token,
        address to
    ) external whenNotPaused onlyRole(GOVERNANCE_ROLE) {
        require(to != address(0), "Address is 0x000");
        if (fees[address(token)] != 0) {
            token.safeTransfer(to, fees[address(token)]);
            fees[address(token)] = 0;
        }
    }

    // PAUSABLE FUNCTIONS ***/
    function pause() external onlyRole(GOVERNANCE_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(GOVERNANCE_ROLE) {
        _unpause();
    }

    // Nonce functions ***/
    /**
     * @dev "Consume a nonce": return the current value and increment.
     *
     */
    function _useNonce(
        address owner
    ) internal virtual returns (uint256 current) {
        Counters.Counter storage nonce = _nonces[owner];
        current = nonce.current();
        nonce.increment();
    }

    /**
     * @notice Relays to nodes to transfers an ERC20 token cross-chain
     * @param to address on other chain to bridge assets to
     * @param chainId which chain to bridge assets onto
     * @param token ERC20 compatible token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function depositToken(
        address to,
        uint256 chainId,
        IERC20 token,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        IFToken fToken = IFToken(tokenToWTokenMap[address(token)]);
        if (address(fToken) == address(0)) revert NOT_SUPPORTED_TOKEN();

        emit TokenDeposited(to, chainId, token, amount);
        token.safeTransferFrom(msg.sender, address(this), amount);
        token.approve(address(fToken), amount);
        fToken.deposit(amount);
        fToken.burn(amount);
    }

    /**
     * @notice Relays to nodes that (typically) a wrapped synAsset ERC20 token has been burned and the underlying needs to be redeeemed on the native chain
     * @param to address on other chain to redeem underlying assets to
     * @param chainId which underlying chain to bridge assets onto
     * @param token ERC20 compatible token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function redeemToken(
        address to,
        uint256 chainId,
        IERC20 token,
        uint256 amount,
        address _signer,
        bytes32 r,
        bytes32 vs
    ) external nonReentrant whenNotPaused {
        uint256 blockChainId = block.chainid;
        if (chainId != blockChainId) revert WRONG_CHAIN();

        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_TOKEN_TYPEHASH,
                to,
                chainId,
                address(token),
                amount,
                _useNonce(_signer)
            )
        );

        bytes32 hash = _hashTypedDataV4(structHash);

        address signer = ECDSA.recover(hash, r, vs);

        if (!hasRole(GUARDIAN_ROLE, signer)) revert WRONGE_SIG();
        if (signer != _signer) revert WRONGE_SIG();

        _redeemToken(to, chainId, token, amount);
    }

    function _redeemToken(
        address to,
        uint256 chainId,
        IERC20 token,
        uint256 amount
    ) private {
        IFToken fToken = IFToken(tokenToWTokenMap[address(token)]);
        emit TokenRedeemed(to, chainId, token, amount);

        fToken.mint(address(this), amount);
        fToken.withdraw(amount);
        token.transfer(to, amount);
    }

    /**
     * @notice Relays to nodes to transfers an ERC20 token cross-chain
     * @param to address on other chain to bridge assets to
     * @param chainId which chain to bridge assets onto
     * @param fToken The wrapped file token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function depositFToken(
        address to,
        uint256 chainId,
        IFToken fToken,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        emit FTokenDeposited(to, chainId, fToken, amount);
        fToken.burn(amount);
    }

    /**
     * @notice Relays to nodes that (typically) a wrapped synAsset ERC20 token has been burned and the underlying needs to be redeeemed on the native chain
     * @param to address on other chain to redeem underlying assets to
     * @param chainId which underlying chain to bridge assets onto
     * @param fToken The wrapped file token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function redeemFToken(
        address to,
        uint256 chainId,
        IFToken fToken,
        uint256 amount,
        address _signer,
        bytes32 r,
        bytes32 vs
    ) external nonReentrant whenNotPaused {
        uint256 blockChainId = block.chainid;
        if (chainId != blockChainId) revert WRONG_CHAIN();

        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_F_TOKEN_TYPEHASH,
                to,
                chainId,
                address(fToken),
                amount,
                _useNonce(_signer)
            )
        );

        bytes32 hash = _hashTypedDataV4(structHash);

        address signer = ECDSA.recover(hash, r, vs);

        if (!hasRole(GUARDIAN_ROLE, signer)) revert WRONGE_SIG();
        if (signer != _signer) revert WRONGE_SIG();

        _redeemFToken(to, chainId, fToken, amount);
    }

    function _redeemFToken(
        address to,
        uint256 chainId,
        IFToken fToken,
        uint256 amount
    ) private {
        emit FTokenRedeemed(to, chainId, fToken, amount);

        fToken.mint(to, amount);
    }

    /**
     * @notice Relays to nodes to transfers an ERC20 token cross-chain
     * @param to address on other chain to bridge assets to
     * @param chainId which chain to bridge assets onto
     **/
    function depositNativeToken(
        address to,
        uint256 chainId
    ) external payable nonReentrant whenNotPaused {
        emit NativeTokenDeposited(to, chainId, msg.value);
        IWETH9(WFIL_ADDRESS).deposit();
    }

    /**
     * @notice Relays to nodes that (typically) a wrapped synAsset ERC20 token has been burned and the underlying needs to be redeeemed on the native chain
     * @param to address on other chain to redeem underlying assets to
     * @param chainId which underlying chain to bridge assets onto
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function redeemNativeToken(
        address to,
        uint256 chainId,
        uint256 amount,
        address _signer,
        bytes32 r,
        bytes32 vs
    ) external nonReentrant whenNotPaused {
        uint256 blockChainId = block.chainid;
        if (chainId != blockChainId) revert WRONG_CHAIN();

        bytes32 structHash = keccak256(
            abi.encode(
                _REDEEM_NATIVE_TOKEN_TYPEHASH,
                to,
                chainId,
                amount,
                _useNonce(_signer)
            )
        );

        bytes32 hash = _hashTypedDataV4(structHash);

        address signer = ECDSA.recover(hash, r, vs);

        if (!hasRole(GUARDIAN_ROLE, signer)) revert WRONGE_SIG();
        if (signer != _signer) revert WRONGE_SIG();

        _redeemNativeToken(to, chainId, amount);
    }

    function _redeemNativeToken(
        address to,
        uint256 chainId,
        uint256 amount
    ) private {
        emit NativeTokenRedeemed(to, chainId, amount);
        (bool success, ) = payable(to).call{value: msg.value}("");

        if (!success) revert TRANSFER_FAILED();
    }
}

interface IWETH9 {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);

    function balanceOf(address) external view returns (uint256);

    function allowance(address, address) external view returns (uint256);

    receive() external payable;

    function deposit() external payable;

    function withdraw(uint256 wad) external;

    function totalSupply() external view returns (uint256);

    function approve(address guy, uint256 wad) external returns (bool);

    function transfer(address dst, uint256 wad) external returns (bool);

    function transferFrom(
        address src,
        address dst,
        uint256 wad
    ) external returns (bool);
}

interface IERC20Mintable is IERC20 {
    function mint(address to, uint256 amount) external;
}

interface IFToken {
    function deposit(uint256 amount) external;

    function withdraw(uint256 amount) external;

    function burn(uint256 amount) external;

    function mint(address to, uint256 amount) external;
}
","keccak256":"0xfdd8c7ebdbd35e70448993d1fe0d7059b30b40f67c6d6f9dd07b60573cc61b85","license":"MIT"},"contracts/bridge/NodeManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

contract NodeManagement is AccessControlUpgradeable {
    bytes32 public constant GUARDIAN_ROLE = keccak256("GUARDIAN_ROLE");
    bytes32 public constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE");

    // Status of the guardian.
    // Active means the guardian is active.
    // Closed means the guardian was closed happily.
    // Terminated means the guardian was closed due to misbehavior.
    enum Status {
        Active,
        Closed,
        Terminated
    }

    // Minimum number of guardian members required to produce a signature.
    uint256 public acceptebleThreshold;

    // The current status of the guardian.
    // If the guardian is Active members monitor it and support requests from the
    // guardian owner.
    // If the owner decides to close the guardian the flag is set to Closed.
    // If the owner seizes member bonds the flag is set to Terminated.
    Status internal status;

    // Notification that the guardian was closed by the owner.
    // Members no longer need to support this guardian.
    event guardianClosed();

    // Notification that the guardian has been terminated by the owner.
    // Members no longer need to support this guardian.
    event guardianTerminated();

    /// @notice Closes guardian when owner decides that they no longer need it.
    /// Releases bonds to the guardian members.
    /// @dev The function can be called only by the owner of the guardian and only
    /// if the guardian has not been already closed.
    function closeguardian() public onlyRole(GOVERNANCE_ROLE) onlyWhenActive {
        markAsClosed();
    }

    /// @notice Returns true if the guardian is active.
    /// @return true if the guardian is active, false otherwise.
    function isActive() public view returns (bool) {
        return status == Status.Active;
    }

    /// @notice Returns true if the guardian is closed and members no longer support
    /// this guardian.
    /// @return true if the guardian is closed, false otherwise.
    function isClosed() public view returns (bool) {
        return status == Status.Closed;
    }

    /// @notice Returns true if the guardian has been terminated.
    /// guardian is terminated when bonds are seized and members no longer support
    /// this guardian.
    /// @return true if the guardian has been terminated, false otherwise.
    function isTerminated() public view returns (bool) {
        return status == Status.Terminated;
    }

    /// @notice Marks the guardian as closed.
    /// guardian can be marked as closed only when there is no signing in progress
    /// or the requested signing process has timed out.
    function markAsClosed() internal {
        status = Status.Closed;
        emit guardianClosed();
    }

    /// @notice Marks the guardian as terminated.
    /// guardian can be marked as terminated only when there is no signing in progress
    /// or the requested signing process has timed out.
    function markAsTerminated() internal {
        status = Status.Terminated;
        emit guardianTerminated();
    }

    /// @notice Terminates the guardian.
    function terminateguardian() internal {
        markAsTerminated();
    }

    /// @notice Checks if the guardian is currently active.
    /// @dev Throws an error if called when the guardian has been already closed.
    modifier onlyWhenActive() {
        require(isActive(), "guardian is not active");
        _;
    }
}
